		private function candidate_edit(){
			if($this->get_request_method() != "POST"){ 
				$data["status"]= false;
				$this->response($this->json($data),400); 
			}
			$user = $datas["id"];
			$disponibility = 1;
			$email_alias = generate_email_alias($datas["email"]);
			$status = 'active';
			$longitude = $datas["longitude"];
			$latitude = $datas["latitude"];
			$birthday = $datas["birthday"];
			$age = (time() - strtotime($birthday)) / 3600 / 24 / 365;
			$photo = $datas["photo"];
			if(empty($photo)){
				switch($datas["genre"]){
					case 'male':
					if($age<30){$photo = AVATAR_YOUNG_MALE;}
					if($age>29){$photo = AVATAR_MALE;}
					if($age>60){$photo = AVATAR_OLD_MALE;}
					break;
					case 'female':
					if($age<30){$photo = AVATAR_YOUNG_FEMALE;}
					if($age>29){$photo = AVATAR_FEMALE;}
					if($age>60){$photo = AVATAR_OLD_FEMALE;}
					break;
					default:
					$photo = AVATAR_NEUTRE;
				}
			}
			$address = $datas["address"];
			$city = $datas["city"];
			$sql = "UPDATE candidates
			SET user='".$user."',
			photo='".$photo."',
			disponibility='".$disponibility."',
			email_alias='".$email_alias."',
			status='".$status."',
			longitude='".$longitude."',
			latitude='".$latitude."',
			birthday='".$birthday."',
			city='".$city."',
			address='".$address."'
			WHERE id='".$datas["id"]."'";
			$result = $this->db->query($sql);
		}
		
