		private function generate_invoice_month(){
		
			$customer_id = $datas['customer'];
			$period = $datas['period'];
			
			if(empty($period)){
				$data["status"] = false;
				$data["message"] = "La période ne peut être vide.";
			   	$this->response($this->json($data),204);
			}
			
			if(empty($customer_id)){
				$data["status"] = false;
				$data["message"] = "Le client ne peut être nul ou vide.";
			   	$this->response($this->json($data),204);
			}
						
			// On récupère les informations des courses du mois
			$query = "SELECT id, delivery_address, price, date_start, date_end  
			FROM run 
			WHERE customer='".$customer_id."' 
			AND date_end LIKE '".$period."-%'
			AND invoiced='0'
			AND status='finished'";
			$result_run = $this->db->query($query);
			if($result_run){
				$nb_run = $result_run->num_rows;
			}
			else{
				$nb_run = "Erreur";
			}
			if($nb_run < 1){
				// Si aucune course on s'arrête là
				$data["status"] = false;
				$data["message"] = "Aucune course à facturer pour le ".$period." (".$nb_run.").";
			   	$this->response($this->json($data),204);
			}

			//On récupère les informations du client
			$query = "SELECT * FROM customer WHERE id='".$customer_id."'";
			$result = $this->db->query($query);
			$customer = $result->fetch_array();
	
			// Je génére un nouveau numéro de facture
			//$num_invoice = $this->generate_invoice_number();
			$status = 'draft';
			
			// Insertion de l'entête de la facture
			$amount_ttc = 0;
			$amount_ht = 0;
			$amount_vat = 0;
			$query = "INSERT INTO invoice
			(date,number,customer,description,messenger,amount_ht,amount_vat,amount_ttc,status)
			VALUES('".date("Y-m-d")."','".$num_invoice."','".$customer_id."','".utf8_decode("Course(s) réalisée(s) pour la période du ".$period)."','','".$amount_ht."','','','".$status."')
			";
			$result = $this->db->query($query);
			$id_invoice = $this->db->insert_id;
			
			// Insertion des lignes de facture
			$i = 0;
			while($run = $result_run->fetch_assoc()){
				//$run = $row[$i];
				$amount_ttc += $run["price"];
				$amount_ht = round($run["price"]/1.2,2);
				$amount_vat = $run["price"] - $amount_ht;
				$date_end = explode(" ",$run["date_end"]);
				$date_end = date_mysql_fr($date_end[0]);
				$query = "INSERT INTO invoice_row
				(invoice,description,amount_ht,quantity,total_ht,total_tva,total_ttc)
				VALUES('".$id_invoice."','".utf8_decode("Course #".$run["id"]." livré le ".$date_end." (".$run["delivery_address"].")")." ','".$amount_ht."','1','".$amount_ht."','".$amount_vat."','".$run["price"]."')
				";
				$result = $this->db->query($query);
				// On met
				$query_update_run = "UPDATE run SET invoiced='1' WHERE id='".$run["id"]."'";
				$result_update_run = $this->db->query($query_update_run);
				$i++;
			}
			$amount_ht = round($amount_ttc/1.2,2);
			$amount_vat = $amount_ttc - $amount_ht;
			
			// Je demande le paiement à Stripe si le client a payé par CB
			
			if($customer["payment_choice"] == "CB" && !empty($customer["stripe_token"])){
				// Je demande le paiement à Stripe
				$datas["stripe_token"] = $customer["stripe_token"];
				$datas["amount_ttc"] = $run["price"];
				//$payment = $this->payment_save_card($datas);
				if($payment["status"] == true){
					$status = "paid";
				}
				else{
					$status = "valid";
				}
			}
			else{
				$status = "draft";
			}
			
			// On met jour le total de la facture et son status
			$query_update_invoice = "UPDATE invoice
			SET amount_ht='".$amount_ht."',
			amount_vat='".$amount_vat."',
			amount_ttc='".$amount_ttc."',
			status='".$status."'
			WHERE id='".$id_invoice."'
			";
			$result_update_invoice = $this->db->query($query_update_invoice);
			
			
			// On génére aussi le paiement si celui ci a été fait
			if($status == "paid"){
				$query	= "INSERT INTO payment (date,run,invoice,amount,information)
				VALUES ('".date("Y-m-d H:i:s")."','".$run["id"]."','".$id_invoice."','".$amount_ttc."','".$payment["id"]."')
				";
				$result = $this->db->query($query);
			}
						
			// Envoi d'un email au client avec la facture
			$this->mail->Subject = "Votre Facture N°".$id_invoice."";
			$body = "Bonjour ".$customer["firstname"].",<br><br>Nous vous prions de trouver en pièce jointe la facture n°".$id_invoice." d'un montant de ".$amount_ttc."€ concernant les courses effectués pour le ".$month."/".$year.".<br><br>Nous restons à votre disposition si vous avez des questions concernant ce document.<br><br>Dans l'attente de pouvoir réaliser une prochaine course pour vous!<br><br>Nous vous remercions pour votre confiance.<br><br>Cordialement<br><br>L'équipe Lelien";
			$this->mail->Body = $body;
			$this->mail->AddAddress($customer["email"]);
			//$this->mail->AddAttachment($_FILES['uploaded_file']['tmp_name'],$_FILES['uploaded_file']['name']);
			$this->mail->send();
			
			if($result){
				$data["status"] = true;
				$data["invoice"] = $id_invoice;
			}
			else{
				$data["status"] = false;
			}
			$this->response($this->json($data), 200);
		}
		
