		private function generate_invoice_number(){
			$query = "SELECT max(number) as max_number FROM invoice";
			$result = $this->db->query($query);
			$data = $result->fetch_array();
			$number = $data["max_number"]+1;
			$number = str_pad($number, 8, "0", STR_PAD_LEFT);
			return $number;
		}
		
